<template>
<div class="event">
	<nuxt-link :to="'/events/' + event.slug">
		<div class="event__image">
			<img src="data:image/jpeg;base64,"/>
		</div>
		<div class="event__details">
			<p class="event__details__text__name events__details__name">{{event.name}}</p>		
			<p class="event__details__text">Posted on: {{event.createdAt | normalizeDate}}</p>
		</div>
	</nuxt-link>
    
</div>
</template>


<script>
import moment from 'moment';
export default {
	props: ['event'],
	filters: {
		uppercase: function(value) {
			if (!value) {
				return '';
			}
			
			return value.toString().toUpperCase();
		},
		normalizeDate: function(date) {
			let time = moment(date).format('LLLL');   // Mon, Jun 9 2014 9:32 PM
			time = time.split(' ');
			time[3] = '';
			return time.join(" ");
		}
	}
}

</script>

